import React, { useState, useEffect } from 'react';
import { Database, FileText, Settings, Play, RotateCcw, Eye, Download } from 'lucide-react';
import DatabaseNormalization from '../components/phase2/DatabaseNormalization';
import StatuteNameNormalizer from '../components/phase2/StatuteNameNormalizer';
import StructureCleaner from '../components/phase2/StructureCleaner';
import SortingInterface from '../components/phase2/SortingInterface';
import ProgressTracker from '../components/phase2/ProgressTracker';
import ResultsPreview from '../components/phase2/ResultsPreview';
import NormalizationHistory from '../components/phase2/NormalizationHistory';

// Configuration storage key for localStorage
const CONFIG_STORAGE_KEY = 'lawchronicle_phase2_config';

// Default configuration
const DEFAULT_CONFIG = {
  source_db: 'Statutes',
  source_collection: 'raw_statutes',
  target_db: 'Statutes',
  target_collection: 'normalized_statutes',
  mongo_uri: 'mongodb://localhost:27017'
};

const Phase2: React.FC = () => {
  const [activeTab, setActiveTab] = useState('overview');
  const [normalizationStatus, setNormalizationStatus] = useState<any>(null);
  const [isLoading, setIsLoading] = useState(false);
  
  // Load configuration from localStorage or use defaults
  const [config, setConfig] = useState(() => {
    try {
      const savedConfig = localStorage.getItem(CONFIG_STORAGE_KEY);
      if (savedConfig) {
        const parsed = JSON.parse(savedConfig);
        // Merge with defaults to ensure all required fields exist
        return { ...DEFAULT_CONFIG, ...parsed };
      }
    } catch (error) {
      console.warn('Failed to load saved configuration:', error);
    }
    return DEFAULT_CONFIG;
  });

  // Save configuration to localStorage whenever it changes
  useEffect(() => {
    try {
      localStorage.setItem(CONFIG_STORAGE_KEY, JSON.stringify(config));
    } catch (error) {
      console.warn('Failed to save configuration:', error);
    }
  }, [config]);

  // Update config when database/collection selection changes
  const updateConfig = (updates: Partial<typeof config>) => {
    setConfig((prev: typeof config) => {
      const newConfig = { ...prev, ...updates };
      
      // Validate configuration
      const validatedConfig = validateConfig(newConfig);
      
      // Also save to localStorage immediately
      try {
        localStorage.setItem(CONFIG_STORAGE_KEY, JSON.stringify(validatedConfig));
      } catch (error) {
        console.warn('Failed to save configuration:', error);
      }
      return validatedConfig;
    });
  };

  // Validate configuration and provide defaults for missing values
  const validateConfig = (configToValidate: any) => {
    return {
      source_db: configToValidate.source_db || DEFAULT_CONFIG.source_db,
      source_collection: configToValidate.source_collection || DEFAULT_CONFIG.source_collection,
      target_db: configToValidate.target_db || DEFAULT_CONFIG.target_db,
      target_collection: configToValidate.target_collection || DEFAULT_CONFIG.target_collection,
      mongo_uri: configToValidate.mongo_uri || DEFAULT_CONFIG.mongo_uri
    };
  };

  // Reset configuration to defaults
  const resetConfig = () => {
    setConfig(DEFAULT_CONFIG);
    try {
      localStorage.removeItem(CONFIG_STORAGE_KEY);
    } catch (error) {
      console.warn('Failed to remove saved configuration:', error);
    }
  };

  const tabs = [
    { id: 'overview', name: 'Overview', icon: Database, description: 'Normalization dashboard and status' },
    { id: 'normalization', name: 'Normalization', icon: Settings, description: 'Execute normalization process' },
    { id: 'statute-names', name: 'Statute Names', icon: FileText, description: 'Clean and standardize names' },
    { id: 'structure', name: 'Structure', icon: Settings, description: 'Clean database structure' },
    { id: 'sorting', name: 'Sorting', icon: FileText, description: 'Alphabetical sorting tools' },
    { id: 'progress', name: 'Progress', icon: Play, description: 'Track normalization progress' },
    { id: 'results', name: 'Results', icon: Eye, description: 'Preview normalized data' },
    { id: 'history', name: 'History', icon: RotateCcw, description: 'Process history and audit' }
  ];

  useEffect(() => {
    // Load initial normalization status
    fetchNormalizationStatus();
  }, []);

  const fetchNormalizationStatus = async () => {
    try {
      const response = await fetch('/api/v1/phase2/normalization-status');
      const data = await response.json();
      if (data.success) {
        setNormalizationStatus(data.status);
      }
    } catch (error) {
      console.error('Failed to fetch normalization status:', error);
    }
  };

  const handleTabChange = (tabId: string) => {
    setActiveTab(tabId);
  };

  const renderTabContent = () => {
    switch (activeTab) {
      case 'overview':
        return (
          <div className="space-y-6">
            {/* Status Overview */}
            <div className="bg-white rounded-lg shadow p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">Normalization Status</h2>
              {normalizationStatus ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                  <div className="bg-blue-50 p-4 rounded-lg">
                    <div className="flex items-center">
                      <Database className="h-8 w-8 text-blue-600" />
                      <div className="ml-3">
                        <p className="text-sm font-medium text-blue-900">Raw Statutes</p>
                        <p className="text-2xl font-bold text-blue-600">{normalizationStatus.raw_statutes_count.toLocaleString()}</p>
                      </div>
                    </div>
                  </div>
                  
                  <div className="bg-green-50 p-4 rounded-lg">
                    <div className="flex items-center">
                      <FileText className="h-8 w-8 text-green-600" />
                      <div className="ml-3">
                        <p className="text-sm font-medium text-green-900">Normalized</p>
                        <p className="text-2xl font-bold text-green-600">{normalizationStatus.normalized_statutes_count.toLocaleString()}</p>
                      </div>
                    </div>
                  </div>
                  
                  <div className="bg-purple-50 p-4 rounded-lg">
                    <div className="flex items-center">
                      <Settings className="h-8 w-8 text-purple-600" />
                      <div className="ml-3">
                        <p className="text-sm font-medium text-purple-900">Cleaned</p>
                        <p className="text-2xl font-bold text-purple-600">{normalizationStatus.cleaned_statutes_count.toLocaleString()}</p>
                      </div>
                    </div>
                  </div>
                  
                  <div className="bg-orange-50 p-4 rounded-lg">
                    <div className="flex items-center">
                      <FileText className="h-8 w-8 text-orange-600" />
                      <div className="ml-3">
                        <p className="text-sm font-medium text-orange-900">Sorted</p>
                        <p className="text-2xl font-bold text-orange-600">{normalizationStatus.sorted_statutes_count.toLocaleString()}</p>
                      </div>
                    </div>
                  </div>
                </div>
              ) : (
                <div className="text-center py-8">
                  <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto mb-4"></div>
                  <p className="text-gray-600">Loading normalization status...</p>
                </div>
              )}
            </div>

            {/* Configuration Management */}
            <div className="bg-white rounded-lg shadow p-6">
              <div className="flex items-center justify-between mb-4">
                <h2 className="text-xl font-semibold text-gray-900">Current Configuration</h2>
                <button
                  onClick={resetConfig}
                  className="px-3 py-2 text-sm font-medium text-red-600 bg-red-50 border border-red-200 rounded-md hover:bg-red-100 focus:outline-none focus:ring-2 focus:ring-red-500"
                >
                  Reset to Defaults
                </button>
              </div>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <h3 className="text-sm font-medium text-gray-700 mb-2">Source Database</h3>
                  <div className="bg-gray-50 p-3 rounded-md">
                    <p className="text-sm text-gray-900"><span className="font-medium">Database:</span> {config.source_db}</p>
                    <p className="text-sm text-gray-900"><span className="font-medium">Collection:</span> {config.source_collection}</p>
                  </div>
                </div>
                <div>
                  <h3 className="text-sm font-medium text-gray-700 mb-2">Target Database</h3>
                  <div className="bg-gray-50 p-3 rounded-md">
                    <p className="text-sm text-gray-900"><span className="font-medium">Database:</span> {config.target_db}</p>
                    <p className="text-sm text-gray-900"><span className="font-medium">Collection:</span> {config.target_collection}</p>
                  </div>
                </div>
              </div>
              <div className="mt-4">
                <h3 className="text-sm font-medium text-gray-700 mb-2">MongoDB URI</h3>
                <div className="bg-gray-50 p-3 rounded-md">
                  <p className="text-sm text-gray-900 font-mono">{config.mongo_uri}</p>
                </div>
              </div>
              <div className="mt-4 text-sm text-gray-600">
                <p>💡 <strong>Tip:</strong> Configuration changes are automatically saved and persist across page refreshes. Use the tabs below to modify settings.</p>
              </div>
            </div>

            {/* Process Flow */}
            <div className="bg-white rounded-lg shadow p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">Normalization Process Flow</h2>
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div className="text-center">
                  <div className="bg-blue-100 rounded-full h-16 w-16 flex items-center justify-center mx-auto mb-3">
                    <Database className="h-8 w-8 text-blue-600" />
                  </div>
                  <h3 className="font-medium text-gray-900">1. Raw Statutes</h3>
                  <p className="text-sm text-gray-600">Source data collection</p>
                </div>
                
                <div className="text-center">
                  <div className="bg-green-100 rounded-full h-16 w-16 flex items-center justify-center mx-auto mb-3">
                    <FileText className="h-8 w-8 text-green-600" />
                  </div>
                  <h3 className="font-medium text-gray-900">2. Normalize Names</h3>
                  <p className="text-sm text-gray-600">Clean statute names</p>
                </div>
                
                <div className="text-center">
                  <div className="bg-purple-100 rounded-full h-16 w-16 flex items-center justify-center mx-auto mb-3">
                    <Settings className="h-8 w-8 text-purple-600" />
                  </div>
                  <h3 className="font-medium text-gray-900">3. Clean Structure</h3>
                  <p className="text-sm text-gray-600">Standardize fields</p>
                </div>
                
                <div className="text-center">
                  <div className="bg-orange-100 rounded-full h-16 w-16 flex items-center justify-center mx-auto mb-3">
                    <FileText className="h-8 w-8 text-orange-600" />
                  </div>
                  <h3 className="font-medium text-gray-900">4. Sort Data</h3>
                  <p className="text-sm text-gray-600">Alphabetical order</p>
                </div>
              </div>
            </div>

            {/* Quick Actions */}
            <div className="bg-white rounded-lg shadow p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">Quick Actions</h2>
              <div className="flex flex-wrap gap-3">
                <button
                  onClick={() => setActiveTab('normalization')}
                  className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
                >
                  <Play className="h-4 w-4 mr-2" />
                  Start Normalization
                </button>
                
                <button
                  onClick={() => setActiveTab('results')}
                  className="inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
                >
                  <Eye className="h-4 w-4 mr-2" />
                  View Results
                </button>
                
                <button
                  onClick={() => setActiveTab('progress')}
                  className="inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
                >
                  <Play className="h-4 w-4 mr-2" />
                  Check Progress
                </button>
              </div>
            </div>
          </div>
        );
      
      case 'normalization':
        return <DatabaseNormalization config={config} onStatusUpdate={fetchNormalizationStatus} onConfigUpdate={updateConfig} />;
      
      case 'statute-names':
        return <StatuteNameNormalizer config={config} />;
      
      case 'structure':
        return <StructureCleaner config={config} />;
      
      case 'sorting':
        return <SortingInterface config={config} />;
      
      case 'progress':
        return <ProgressTracker config={config} />;
      
      case 'results':
        return <ResultsPreview config={config} />;
      
      case 'history':
        return <NormalizationHistory config={config} />;
      
      default:
        return <div>Select a tab to get started</div>;
    }
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-6 py-4 border-b border-gray-200">
          <h1 className="text-2xl font-bold text-gray-900">Phase 2: Database Normalization Dashboard</h1>
          <p className="text-gray-600 mt-1">
            Enhanced normalization engine with comprehensive tools for cleaning, structuring, and sorting legal documents
          </p>
        </div>
        
        {/* Tab Navigation */}
        <div className="px-6 py-4">
          <nav className="flex space-x-8 overflow-x-auto">
            {tabs.map((tab) => (
              <button
                key={tab.id}
                onClick={() => handleTabChange(tab.id)}
                className={`flex items-center space-x-2 px-3 py-2 rounded-md text-sm font-medium whitespace-nowrap transition-colors duration-200 ${
                  activeTab === tab.id
                    ? 'bg-primary-100 text-primary-700 border border-primary-200'
                    : 'text-gray-500 hover:text-gray-700 hover:bg-gray-100'
                }`}
              >
                <tab.icon className="h-4 w-4" />
                <span>{tab.name}</span>
              </button>
            ))}
          </nav>
        </div>
      </div>

      {/* Tab Content */}
      <div className="bg-white shadow rounded-lg">
        {renderTabContent()}
      </div>
    </div>
  );
};

export default Phase2;
