              <button onClick={previewValidationMetadata} disabled={isProcessing || (!cleanAllBatches && selectedBatches.length===0)} className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 disabled:opacity-50">Preview Metadata</button>
              <button onClick={fetchStatus} className="bg-gray-600 text-white px-6 py-2 rounded-md hover:bg-gray-700">Refresh Status</button>
            </div>
          </div>
        )}

        {activeTab === 'preview' && (
          <div className="space-y-4">
