              </button>
            </div>
            
            {batchPreview ? (
              <div className="space-y-4">
                {batchPreview.batches.length > 0 ? (
                  batchPreview.batches.map((batch: any, index: number) => (
                    <div key={index} className="bg-gray-50 p-4 rounded-lg">
