import React, { useState, useEffect } from 'react';
import { Eye, Download, RefreshCw, AlertTriangle, CheckCircle } from 'lucide-react';
import axios from 'axios';

interface NormalizedDocument {
  _id: string;
  statute_name?: string;
  act_ordinance?: string;
  year?: string | number;
  date?: string;
  section_number?: string;
  section_definition?: string;
  section?: string;
  content?: string;
  html_content?: string;
  rag_content?: string;
  category?: string;
  source?: string;
  province?: string;
  statute_type?: string;
  normalized_at?: string;
  cleaning_version?: string;
  sort_order?: number;
  sorted_at?: string;
  [key: string]: any;
}

interface PreviewData {
  stage: string;
  documents: NormalizedDocument[];
  total_count: number;
  previewed_at: string;
}

interface ResultsPreviewProps {
  config: any;
}

const ResultsPreview: React.FC<ResultsPreviewProps> = ({ config }) => {
  const [previewData, setPreviewData] = useState<PreviewData | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string>('');
  const [selectedDocument, setSelectedDocument] = useState<NormalizedDocument | null>(null);
  const [showRawComparison, setShowRawComparison] = useState(false);

  useEffect(() => {
    fetchPreviewData();
  }, []);

  const fetchPreviewData = async () => {
    setIsLoading(true);
    setError('');
    
    try {
      const response = await axios.get('/api/v1/phase2/preview-normalized?limit=20');
      
      if (response.data.success) {
        setPreviewData(response.data);
      } else {
        setError(response.data.message || 'Failed to fetch preview data');
      }
    } catch (err: any) {
      setError(err.response?.data?.detail || err.message || 'Failed to fetch preview data');
    } finally {
      setIsLoading(false);
    }
  };

  const getStageColor = (stage: string) => {
    switch (stage) {
      case 'sorted':
        return 'bg-orange-100 text-orange-800';
      case 'cleaned':
        return 'bg-purple-100 text-purple-800';
      case 'normalized':
        return 'bg-green-100 text-green-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getStageIcon = (stage: string) => {
    switch (stage) {
      case 'sorted':
        return <CheckCircle className="h-5 w-5 text-orange-600" />;
      case 'cleaned':
        return <CheckCircle className="h-5 w-5 text-purple-600" />;
      case 'normalized':
        return <CheckCircle className="h-5 w-5 text-green-600" />;
      default:
        return <AlertTriangle className="h-5 w-5 text-gray-600" />;
    }
  };

  const formatFieldValue = (value: any, maxLength: number = 100) => {
    if (value === null || value === undefined) {
      return <span className="text-gray-400 italic">null</span>;
    }
    
    const stringValue = String(value);
    if (stringValue.length <= maxLength) {
      return stringValue;
    }
    
    return (
      <span title={stringValue}>
        {stringValue.substring(0, maxLength)}...
      </span>
    );
  };

  const getFieldDisplayName = (fieldName: string) => {
    const fieldMappings: { [key: string]: string } = {
      'statute_name': 'Statute Name',
      'act_ordinance': 'Act/Ordinance',
      'year': 'Year',
      'date': 'Date',
      'section': 'Section',
      'section_number': 'Section Number',
      'section_definition': 'Section Definition',
      'content': 'Content',
      'html_content': 'HTML Content',
      'rag_content': 'RAG Content',
      'category': 'Category',
      'source': 'Source',
      'province': 'Province',
      'statute_type': 'Statute Type',
      'normalized_at': 'Normalized At',
      'cleaning_version': 'Cleaning Version',
      'sort_order': 'Sort Order',
      'sorted_at': 'Sorted At',
      'original_id': 'Original ID'
    };
    
    return fieldMappings[fieldName] || fieldName.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
  };

  if (isLoading) {
    return (
      <div className="text-center py-12">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto mb-4"></div>
        <p className="text-gray-600">Loading normalized data preview...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-lg p-6">
        <div className="flex items-center">
          <AlertTriangle className="h-5 w-5 text-red-400 mr-2" />
          <h3 className="text-sm font-medium text-red-800">Error Loading Preview</h3>
        </div>
        <p className="text-sm text-red-700 mt-1">{error}</p>
        <button
          onClick={fetchPreviewData}
          className="mt-3 inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-red-700 bg-red-100 hover:bg-red-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
        >
          <RefreshCw className="h-4 w-4 mr-2" />
          Try Again
        </button>
      </div>
    );
  }

  if (!previewData) {
    return (
      <div className="text-center py-12">
        <Eye className="h-12 w-12 text-gray-400 mx-auto mb-4" />
        <h3 className="text-lg font-medium text-gray-900 mb-2">No Normalized Data Available</h3>
        <p className="text-gray-600 mb-4">
          Run the normalization process first to see preview results.
        </p>
        <button
          onClick={fetchPreviewData}
          className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
        >
          <RefreshCw className="h-4 w-4 mr-2" />
          Check Again
        </button>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow p-6">
        <div className="flex items-center justify-between mb-4">
          <div>
            <h2 className="text-xl font-semibold text-gray-900">Normalized Data Preview</h2>
            <p className="text-gray-600 mt-1">
              Preview the results of your normalization process
            </p>
          </div>
          <div className="flex items-center space-x-3">
            <span className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${getStageColor(previewData.stage)}`}>
              {getStageIcon(previewData.stage)}
              <span className="ml-1">{previewData.stage.charAt(0).toUpperCase() + previewData.stage.slice(1)}</span>
            </span>
            <button
              onClick={fetchPreviewData}
              className="inline-flex items-center px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
            >
              <RefreshCw className="h-4 w-4 mr-2" />
              Refresh
            </button>
          </div>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="bg-blue-50 p-4 rounded-lg">
            <p className="text-sm font-medium text-blue-900">Total Documents</p>
            <p className="text-2xl font-bold text-blue-600">{previewData.total_count.toLocaleString()}</p>
          </div>
          
          <div className="bg-green-50 p-4 rounded-lg">
            <p className="text-sm font-medium text-green-900">Preview Sample</p>
            <p className="text-2xl font-bold text-green-600">{previewData.documents.length}</p>
          </div>
          
          <div className="bg-purple-50 p-4 rounded-lg">
            <p className="text-sm font-medium text-purple-900">Last Updated</p>
            <p className="text-sm text-purple-600">
              {new Date(previewData.previewed_at).toLocaleString()}
            </p>
          </div>
        </div>
      </div>

      {/* Document List */}
      <div className="bg-white rounded-lg shadow">
        <div className="px-6 py-4 border-b border-gray-200">
          <h3 className="text-lg font-medium text-gray-900">Document Preview</h3>
          <p className="text-sm text-gray-600 mt-1">
            Click on a document to view detailed information
          </p>
        </div>
        
        <div className="divide-y divide-gray-200">
          {previewData.documents.map((doc, index) => (
            <div
              key={doc._id || index}
              className="px-6 py-4 hover:bg-gray-50 cursor-pointer transition-colors"
              onClick={() => setSelectedDocument(doc)}
            >
              <div className="flex items-center justify-between">
                <div className="flex-1">
                  <h4 className="font-medium text-gray-900">
                    {doc.statute_name || doc.act_ordinance || 'Unnamed Statute'}
                  </h4>
                  <div className="flex items-center space-x-4 mt-1 text-sm text-gray-600">
                    {doc.year && <span>Year: {doc.year}</span>}
                    {doc.section_number && <span>Section: {doc.section_number}</span>}
                    {doc.sort_order && <span>Order: #{doc.sort_order}</span>}
                    {doc.category && <span>Category: {doc.category}</span>}
                  </div>
                </div>
                <div className="flex items-center space-x-2">
                  {doc.normalized_at && (
                    <span className="text-xs text-gray-500">
                      Normalized: {new Date(doc.normalized_at).toLocaleDateString()}
                    </span>
                  )}
                  <Eye className="h-4 w-4 text-gray-400" />
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Document Detail Modal */}
      {selectedDocument && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-11/12 max-w-4xl shadow-lg rounded-md bg-white">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-medium text-gray-900">Document Details</h3>
              <button
                onClick={() => setSelectedDocument(null)}
                className="text-gray-400 hover:text-gray-600"
              >
                <span className="sr-only">Close</span>
                ×
              </button>
            </div>
            
            <div className="space-y-4">
              {/* Basic Information */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {Object.entries(selectedDocument).map(([key, value]) => {
                  if (key === '_id' || key === 'content' || key === 'html_content' || key === 'rag_content') {
                    return null; // Skip these fields for the basic grid
                  }
                  
                  return (
                    <div key={key} className="space-y-1">
                      <label className="block text-sm font-medium text-gray-700">
                        {getFieldDisplayName(key)}
                      </label>
                      <div className="text-sm text-gray-900 bg-gray-50 p-2 rounded border">
                        {formatFieldValue(value, 200)}
                      </div>
                    </div>
                  );
                })}
              </div>
              
              {/* Content Fields */}
              {selectedDocument.content && (
                <div className="space-y-1">
                  <label className="block text-sm font-medium text-gray-700">Content</label>
                  <div className="text-sm text-gray-900 bg-gray-50 p-3 rounded border max-h-40 overflow-y-auto">
                    {selectedDocument.content}
                  </div>
                </div>
              )}
              
              {selectedDocument.html_content && (
                <div className="space-y-1">
                  <label className="block text-sm font-medium text-gray-700">HTML Content</label>
                  <div className="text-sm text-gray-900 bg-gray-50 p-3 rounded border max-h-40 overflow-y-auto">
                    <div dangerouslySetInnerHTML={{ __html: selectedDocument.html_content }} />
                  </div>
                </div>
              )}
            </div>
            
            <div className="mt-6 flex justify-end">
              <button
                onClick={() => setSelectedDocument(null)}
                className="px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Information Panel */}
      <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
        <h3 className="text-sm font-medium text-blue-800 mb-2">About the Preview</h3>
        <ul className="text-sm text-blue-700 space-y-1">
          <li>• <strong>Normalized:</strong> Statute names cleaned and standardized</li>
          <li>• <strong>Cleaned:</strong> Database structure standardized with consistent field names</li>
          <li>• <strong>Sorted:</strong> Documents arranged alphabetically with proper ordering</li>
        </ul>
        <p className="text-xs text-blue-600 mt-2">
          The preview shows a sample of {previewData.documents.length} documents from a total of {previewData.total_count} normalized documents.
        </p>
      </div>
    </div>
  );
};

export default ResultsPreview;
