import React, { useState, useEffect, useCallback } from 'react';
import { ArrowUpDown, Settings, Save, RefreshCw, AlertTriangle, CheckCircle, Plus, X, Eye } from 'lucide-react';

interface SortingRules {
  primary_sort: string;
  secondary_sort: string;
  tertiary_sort: string;
  custom_rules: Array<{
    field: string;
    direction: 'asc' | 'desc';
    priority: number;
    custom_logic?: string;
  }>;
}

interface SortingInterfaceProps {
  config: any;
}

const SortingInterface: React.FC<SortingInterfaceProps> = ({ config }) => {
  const [sortingRules, setSortingRules] = useState<SortingRules>({
    primary_sort: 'statute_name',
    secondary_sort: 'year',
    tertiary_sort: 'section_number',
    custom_rules: []
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState<{ type: 'success' | 'error'; text: string } | null>(null);
  const [showPreview, setShowPreview] = useState(false);
  const [sortPreview, setSortPreview] = useState<any[]>([]);
  const [availableFields, setAvailableFields] = useState<string[]>([
    'statute_name', 'year', 'section_number', 'section_definition', 
    'category', 'province', 'statute_type', 'date', 'promulgation_date'
  ]);

  // Fetch current sorting rules
  const fetchSortingRules = useCallback(async () => {
    setLoading(true);
    try {
      const response = await fetch('/api/v1/phase2/sorting-rules');
      const data = await response.json();
      
      if (data.success) {
        setSortingRules(data.sorting_rules);
      } else {
        setMessage({ type: 'error', text: 'Failed to fetch sorting rules' });
      }
    } catch (error) {
      setMessage({ type: 'error', text: 'Error fetching sorting rules' });
    } finally {
      setLoading(false);
    }
  }, []);

  // Save sorting rules
  const saveSortingRules = async () => {
    try {
      const response = await fetch('/api/v1/phase2/update-sorting-rules', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ sorting_rules: sortingRules })
      });
      
      const data = await response.json();
      
      if (data.success) {
        setMessage({ type: 'success', text: 'Sorting rules saved successfully' });
      } else {
        setMessage({ type: 'error', text: 'Failed to save sorting rules' });
      }
    } catch (error) {
      setMessage({ type: 'error', text: 'Error saving sorting rules' });
    }
  };

  // Add custom sorting rule
  const addCustomRule = () => {
    const newRule = {
      field: '',
      direction: 'asc' as const,
      priority: sortingRules.custom_rules.length + 1,
      custom_logic: ''
    };
    setSortingRules(prev => ({
      ...prev,
      custom_rules: [...prev.custom_rules, newRule]
    }));
  };

  // Remove custom sorting rule
  const removeCustomRule = (index: number) => {
    setSortingRules(prev => ({
      ...prev,
      custom_rules: prev.custom_rules.filter((_, i) => i !== index)
    }));
  };

  // Update custom sorting rule
  const updateCustomRule = (index: number, field: string, value: any) => {
    setSortingRules(prev => ({
      ...prev,
      custom_rules: prev.custom_rules.map((rule, i) => 
        i === index ? { ...rule, [field]: value } : rule
      )
    }));
  };

  // Update main sorting fields
  const updateMainSorting = (field: string, value: string) => {
    setSortingRules(prev => ({ ...prev, [field]: value }));
  };

  // Generate sort preview
  const generateSortPreview = async () => {
    setLoading(true);
    try {
      // Simulate sorting preview with sample data
      const sampleData = [
        { statute_name: 'Apprenticeship Act', year: 2018, section_number: '1' },
        { statute_name: 'Apprenticeship Act', year: 2018, section_number: '2' },
        { statute_name: 'Apprenticeship Act', year: 2019, section_number: '1' },
        { statute_name: 'Banking Act', year: 2017, section_number: '1' },
        { statute_name: 'Banking Act', year: 2017, section_number: '2' },
        { statute_name: 'Civil Code', year: 2020, section_number: '1' },
        { statute_name: 'Civil Code', year: 2020, section_number: '2' },
        { statute_name: 'Civil Code', year: 2020, section_number: '3' }
      ];

      // Apply sorting rules
      let sortedData = [...sampleData];
      
      // Primary sort
      if (sortingRules.primary_sort) {
        sortedData.sort((a, b) => {
          const aVal = a[sortingRules.primary_sort as keyof typeof a];
          const bVal = b[sortingRules.primary_sort as keyof typeof b];
          if (aVal < bVal) return -1;
          if (aVal > bVal) return 1;
          return 0;
        });
      }

      // Secondary sort
      if (sortingRules.secondary_sort && sortingRules.secondary_sort !== sortingRules.primary_sort) {
        sortedData.sort((a, b) => {
          const aPrimary = a[sortingRules.primary_sort as keyof typeof a];
          const bPrimary = b[sortingRules.primary_sort as keyof typeof b];
          if (aPrimary !== bPrimary) return 0; // Only sort if primary is same
          
          const aVal = a[sortingRules.secondary_sort as keyof typeof a];
          const bVal = b[sortingRules.secondary_sort as keyof typeof b];
          if (aVal < bVal) return -1;
          if (aVal > bVal) return 1;
          return 0;
        });
      }

      // Tertiary sort
      if (sortingRules.tertiary_sort && sortingRules.tertiary_sort !== sortingRules.primary_sort && sortingRules.tertiary_sort !== sortingRules.secondary_sort) {
        sortedData.sort((a, b) => {
          const aPrimary = a[sortingRules.primary_sort as keyof typeof a];
          const bPrimary = b[sortingRules.primary_sort as keyof typeof b];
          if (aPrimary !== bPrimary) return 0;
          
          const aSecondary = a[sortingRules.secondary_sort as keyof typeof a];
          const bSecondary = b[sortingRules.secondary_sort as keyof typeof b];
          if (aSecondary !== bSecondary) return 0;
          
          const aVal = a[sortingRules.tertiary_sort as keyof typeof a];
          const bVal = b[sortingRules.tertiary_sort as keyof typeof b];
          if (aVal < bVal) return -1;
          if (aVal > bVal) return 1;
          return 0;
        });
      }

      // Apply custom rules
      sortingRules.custom_rules.forEach(rule => {
        if (rule.field) {
          sortedData.sort((a, b) => {
            const aVal = a[rule.field as keyof typeof a];
            const bVal = b[rule.field as keyof typeof b];
            const multiplier = rule.direction === 'asc' ? 1 : -1;
            if (aVal < bVal) return -1 * multiplier;
            if (aVal > bVal) return 1 * multiplier;
            return 0;
          });
        }
      });

      setSortPreview(sortedData);
      setShowPreview(true);
    } catch (error) {
      setMessage({ type: 'error', text: 'Error generating sort preview' });
    } finally {
      setLoading(false);
    }
  };

  // Get sort direction icon
  const getSortDirectionIcon = (field: string) => {
    if (field === sortingRules.primary_sort) return '1️⃣';
    if (field === sortingRules.secondary_sort) return '2️⃣';
    if (field === sortingRules.tertiary_sort) return '3️⃣';
    return '';
  };

  useEffect(() => {
    fetchSortingRules();
  }, [fetchSortingRules]);

  // Clear message after 5 seconds
  useEffect(() => {
    if (message) {
      const timer = setTimeout(() => setMessage(null), 5000);
      return () => clearTimeout(timer);
    }
  }, [message]);

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-medium text-gray-900">Sorting Configuration</h3>
          <p className="text-sm text-gray-600">
            Configure document sorting rules and custom sort logic
          </p>
        </div>
        <div className="flex space-x-2">
          <button
            onClick={generateSortPreview}
            disabled={loading}
            className="inline-flex items-center px-3 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50"
          >
            <Eye className="h-4 w-4 mr-2" />
            Preview Sort
          </button>
          <button
            onClick={saveSortingRules}
            className="inline-flex items-center px-3 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
          >
            <Save className="h-4 w-4 mr-2" />
            Save Rules
          </button>
        </div>
      </div>

      {/* Message Display */}
      {message && (
        <div className={`rounded-md p-4 ${
          message.type === 'success' ? 'bg-green-50 border border-green-200' : 'bg-red-50 border border-red-200'
        }`}>
          <div className="flex">
            <div className="flex-shrink-0">
              {message.type === 'success' ? (
                <CheckCircle className="h-5 w-5 text-green-400" />
              ) : (
                <AlertTriangle className="h-5 w-5 text-red-400" />
              )}
            </div>
            <div className="ml-3">
              <p className={`text-sm ${
                message.type === 'success' ? 'text-green-800' : 'text-red-800'
              }`}>
                {message.text}
              </p>
            </div>
          </div>
        </div>
      )}

      {/* Main Sorting Configuration */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-6 py-4 border-b border-gray-200">
          <h4 className="text-lg font-medium text-gray-900">Primary Sorting Configuration</h4>
          <p className="text-sm text-gray-600">
            Define the main sorting hierarchy for documents
          </p>
        </div>
        
        <div className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {/* Primary Sort */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Primary Sort Field {getSortDirectionIcon(sortingRules.primary_sort)}
              </label>
              <select
                value={sortingRules.primary_sort}
                onChange={(e) => updateMainSorting('primary_sort', e.target.value)}
                className="block w-full border border-gray-300 rounded-md px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
              >
                <option value="">Select field</option>
                {availableFields.map(field => (
                  <option key={field} value={field}>{field.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}</option>
                ))}
              </select>
              <p className="mt-1 text-xs text-gray-500">Documents will be sorted by this field first</p>
            </div>

            {/* Secondary Sort */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Secondary Sort Field {getSortDirectionIcon(sortingRules.secondary_sort)}
              </label>
              <select
                value={sortingRules.secondary_sort}
                onChange={(e) => updateMainSorting('secondary_sort', e.target.value)}
                className="block w-full border border-gray-300 rounded-md px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
              >
                <option value="">Select field</option>
                {availableFields.map(field => (
                  <option key={field} value={field}>{field.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}</option>
                ))}
              </select>
              <p className="mt-1 text-xs text-gray-500">Used when primary sort values are equal</p>
            </div>

            {/* Tertiary Sort */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Tertiary Sort Field {getSortDirectionIcon(sortingRules.tertiary_sort)}
              </label>
              <select
                value={sortingRules.tertiary_sort}
                onChange={(e) => updateMainSorting('tertiary_sort', e.target.value)}
                className="block w-full border border-gray-300 rounded-md px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
              >
                <option value="">Select field</option>
                {availableFields.map(field => (
                  <option key={field} value={field}>{field.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}</option>
                ))}
              </select>
              <p className="mt-1 text-xs text-gray-500">Final tie-breaker when other sorts are equal</p>
            </div>
          </div>
        </div>
      </div>

      {/* Custom Sorting Rules */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-6 py-4 border-b border-gray-200">
          <div className="flex items-center justify-between">
            <div>
              <h4 className="text-lg font-medium text-gray-900">Custom Sorting Rules</h4>
              <p className="text-sm text-gray-600">
                Define additional sorting logic and custom sort keys
              </p>
            </div>
            <button
              onClick={addCustomRule}
              className="inline-flex items-center px-3 py-1 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-primary-500"
            >
              <Plus className="h-4 w-4 mr-2" />
              Add Rule
            </button>
          </div>
        </div>
        
        <div className="p-6">
          {sortingRules.custom_rules.length === 0 ? (
            <div className="text-center py-8">
              <ArrowUpDown className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-600">No custom sorting rules defined</p>
              <p className="text-sm text-gray-500">Add rules to implement complex sorting logic</p>
            </div>
          ) : (
            <div className="space-y-4">
              {sortingRules.custom_rules.map((rule, index) => (
                <div key={index} className="flex items-center space-x-4 p-4 border border-gray-200 rounded-lg">
                  <div className="flex-1 grid grid-cols-1 md:grid-cols-4 gap-3">
                    <div>
                      <label className="block text-xs font-medium text-gray-700 mb-1">
                        Field
                      </label>
                      <select
                        value={rule.field}
                        onChange={(e) => updateCustomRule(index, 'field', e.target.value)}
                        className="block w-full border border-gray-300 rounded-md px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
                      >
                        <option value="">Select field</option>
                        {availableFields.map(field => (
                          <option key={field} value={field}>{field.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}</option>
                        ))}
                      </select>
                    </div>
                    
                    <div>
                      <label className="block text-xs font-medium text-gray-700 mb-1">
                        Direction
                      </label>
                      <select
                        value={rule.direction}
                        onChange={(e) => updateCustomRule(index, 'direction', e.target.value)}
                        className="block w-full border border-gray-300 rounded-md px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
                      >
                        <option value="asc">Ascending (A→Z)</option>
                        <option value="desc">Descending (Z→A)</option>
                      </select>
                    </div>
                    
                    <div>
                      <label className="block text-xs font-medium text-gray-700 mb-1">
                        Priority
                      </label>
                      <input
                        type="number"
                        value={rule.priority}
                        onChange={(e) => updateCustomRule(index, 'priority', parseInt(e.target.value))}
                        min="1"
                        className="block w-full border border-gray-300 rounded-md px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
                      />
                    </div>
                    
                    <div>
                      <label className="block text-xs font-medium text-gray-700 mb-1">
                        Custom Logic
                      </label>
                      <input
                        type="text"
                        value={rule.custom_logic || ''}
                        onChange={(e) => updateCustomRule(index, 'custom_logic', e.target.value)}
                        placeholder="Optional custom logic"
                        className="block w-full border border-gray-300 rounded-md px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
                      />
                    </div>
                  </div>
                  
                  <button
                    onClick={() => removeCustomRule(index)}
                    className="text-red-600 hover:text-red-900 p-1"
                  >
                    <X className="h-4 w-4" />
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Sort Preview */}
      {showPreview && (
        <div className="bg-white shadow rounded-lg">
          <div className="px-6 py-4 border-b border-gray-200">
            <h4 className="text-lg font-medium text-gray-900">Sort Preview</h4>
            <p className="text-sm text-gray-600">
              Preview of how documents will be sorted with current rules
            </p>
          </div>
          
          <div className="p-6">
            <div className="overflow-hidden">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Order
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Statute Name
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Year
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Section
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {sortPreview.map((doc, index) => (
                    <tr key={index} className="hover:bg-gray-50">
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                          {index + 1}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                        {doc.statute_name}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {doc.year}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {doc.section_number}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}

      {/* Advanced Sorting Options */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-6 py-4 border-b border-gray-200">
          <h4 className="text-lg font-medium text-gray-900">Advanced Sorting Options</h4>
          <p className="text-sm text-gray-600">
            Configure advanced sorting behavior and performance options
          </p>
        </div>
        
        <div className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <h5 className="text-sm font-medium text-gray-900 mb-3">Sorting Behavior</h5>
              <div className="space-y-3">
                <div className="flex items-center space-x-3">
                  <input
                    type="checkbox"
                    id="case-sensitive"
                    className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                  />
                  <label htmlFor="case-sensitive" className="text-sm text-gray-700">
                    Case-sensitive sorting
                  </label>
                </div>
                
                <div className="flex items-center space-x-3">
                  <input
                    type="checkbox"
                    id="numeric-sort"
                    className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                  />
                  <label htmlFor="numeric-sort" className="text-sm text-gray-700">
                    Natural numeric sorting (1, 2, 10, 11)
                  </label>
                </div>
                
                <div className="flex items-center space-x-3">
                  <input
                    type="checkbox"
                    id="locale-aware"
                    className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                  />
                  <label htmlFor="locale-aware" className="text-sm text-gray-700">
                    Locale-aware string sorting
                  </label>
                </div>
              </div>
            </div>
            
            <div>
              <h5 className="text-sm font-medium text-gray-900 mb-3">Performance Options</h5>
              <div className="space-y-3">
                <div className="flex items-center space-x-3">
                  <input
                    type="checkbox"
                    id="create-indexes"
                    className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                  />
                  <label htmlFor="create-indexes" className="text-sm text-gray-700">
                    Create database indexes for sort fields
                  </label>
                </div>
                
                <div className="flex items-center space-x-3">
                  <input
                    type="checkbox"
                    id="batch-sort"
                    className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                  />
                  <label htmlFor="batch-sort" className="text-sm text-gray-700">
                    Enable batch sorting for large datasets
                  </label>
                </div>
                
                <div className="flex items-center space-x-3">
                  <input
                    type="checkbox"
                    id="parallel-sort"
                    className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                  />
                  <label htmlFor="parallel-sort" className="text-sm text-gray-700">
                    Enable parallel sorting (experimental)
                  </label>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Summary */}
      <div className="bg-gray-50 rounded-lg p-4">
        <h4 className="text-sm font-medium text-gray-900 mb-2">Sorting Configuration Summary</h4>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 text-sm">
          <div>
            <span className="text-gray-600">Primary Sort:</span>
            <span className="ml-2 font-medium text-gray-900">
              {sortingRules.primary_sort || 'None'}
            </span>
          </div>
          <div>
            <span className="text-gray-600">Secondary Sort:</span>
            <span className="ml-2 font-medium text-gray-900">
              {sortingRules.secondary_sort || 'None'}
            </span>
          </div>
          <div>
            <span className="text-gray-600">Tertiary Sort:</span>
            <span className="ml-2 font-medium text-gray-900">
              {sortingRules.tertiary_sort || 'None'}
            </span>
          </div>
          <div>
            <span className="text-gray-600">Custom Rules:</span>
            <span className="ml-2 font-medium text-gray-900">
              {sortingRules.custom_rules.length}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SortingInterface;
