import React, { useState, useEffect, useCallback } from 'react';
import { History, RotateCcw, Eye, Download, Filter, Calendar, User, Activity, AlertTriangle, CheckCircle, Clock, RefreshCw, X } from 'lucide-react';

interface NormalizationOperation {
  _id: string;
  operation_type: string;
  status: 'success' | 'failed' | 'in_progress' | 'cancelled';
  details: {
    source_collection?: string;
    target_collection?: string;
    documents_processed?: number;
    errors?: string[];
    warnings?: string[];
    processing_time?: number;
    memory_usage?: number;
  };
  timestamp: string;
  user: string;
  rollback_available?: boolean;
  rollback_status?: 'available' | 'completed' | 'failed' | 'not_available';
}

interface NormalizationHistoryProps {
  config: any;
}

const NormalizationHistory: React.FC<NormalizationHistoryProps> = ({ config }) => {
  const [operations, setOperations] = useState<NormalizationOperation[]>([]);
  const [loading, setLoading] = useState(false);
  const [selectedOperation, setSelectedOperation] = useState<NormalizationOperation | null>(null);
  const [showDetails, setShowDetails] = useState(false);
  const [filters, setFilters] = useState({
    status: 'all',
    operation_type: 'all',
    date_range: '7d',
    user: 'all'
  });
  const [rollbackLoading, setRollbackLoading] = useState<string | null>(null);

  // Fetch normalization history
  const fetchHistory = useCallback(async () => {
    setLoading(true);
    try {
      const response = await fetch('/api/v1/phase2/normalization-history?limit=100');
      const data = await response.json();
      
      if (data.success) {
        // Simulate additional operations for demonstration
        const simulatedOperations: NormalizationOperation[] = [
          ...data.history,
          {
            _id: 'sim_1',
            operation_type: 'statute_name_normalization',
            status: 'success',
            details: {
              source_collection: 'raw_statutes',
              target_collection: 'normalized_statutes',
              documents_processed: 1250,
              processing_time: 45,
              memory_usage: 256
            },
            timestamp: new Date(Date.now() - 2 * 60 * 60 * 1000).toISOString(),
            user: 'admin',
            rollback_available: true,
            rollback_status: 'available'
          },
          {
            _id: 'sim_2',
            operation_type: 'structure_cleaning',
            status: 'success',
            details: {
              source_collection: 'normalized_statutes',
              target_collection: 'cleaned_statutes',
              documents_processed: 1250,
              processing_time: 32,
              memory_usage: 198
            },
            timestamp: new Date(Date.now() - 3 * 60 * 60 * 1000).toISOString(),
            user: 'admin',
            rollback_available: true,
            rollback_status: 'available'
          },
          {
            _id: 'sim_3',
            operation_type: 'alphabetical_sorting',
            status: 'in_progress',
            details: {
              source_collection: 'cleaned_statutes',
              target_collection: 'sorted_statutes',
              documents_processed: 800,
              processing_time: 28,
              memory_usage: 245
            },
            timestamp: new Date(Date.now() - 1 * 60 * 60 * 1000).toISOString(),
            user: 'admin',
            rollback_available: false,
            rollback_status: 'not_available'
          }
        ];
        setOperations(simulatedOperations);
      } else {
        console.error('Failed to fetch normalization history');
      }
    } catch (error) {
      console.error('Error fetching normalization history:', error);
    } finally {
      setLoading(false);
    }
  }, []);

  // Rollback operation
  const rollbackOperation = async (operationId: string) => {
    setRollbackLoading(operationId);
    try {
      const response = await fetch('/api/v1/phase2/rollback', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ operation_id: operationId })
      });
      
      const data = await response.json();
      
      if (data.success) {
        // Update operation status
        setOperations(prev => prev.map(op => 
          op._id === operationId 
            ? { ...op, rollback_status: 'completed' as const }
            : op
        ));
        
        // Refresh history
        fetchHistory();
      } else {
        console.error('Rollback failed:', data.message);
      }
    } catch (error) {
      console.error('Error during rollback:', error);
    } finally {
      setRollbackLoading(null);
    }
  };

  // Get status color
  const getStatusColor = (status: string) => {
    const colors: { [key: string]: string } = {
      'success': 'bg-green-100 text-green-800',
      'failed': 'bg-red-100 text-red-800',
      'in_progress': 'bg-blue-100 text-blue-800',
      'cancelled': 'bg-gray-100 text-gray-800'
    };
    return colors[status] || 'bg-gray-100 text-gray-800';
  };

  // Get operation type icon
  const getOperationIcon = (type: string) => {
    const icons: { [key: string]: React.ReactNode } = {
      'statute_name_normalization': <Activity className="h-5 w-5" />,
      'structure_cleaning': <RotateCcw className="h-5 w-5" />,
      'alphabetical_sorting': <History className="h-5 w-5" />,
      'full_normalization': <Activity className="h-5 w-5" />
    };
    return icons[type] || <Activity className="h-5 w-5" />;
  };

  // Get operation type display name
  const getOperationDisplayName = (type: string) => {
    const names: { [key: string]: string } = {
      'statute_name_normalization': 'Statute Name Normalization',
      'structure_cleaning': 'Structure Cleaning',
      'alphabetical_sorting': 'Alphabetical Sorting',
      'full_normalization': 'Full Normalization'
    };
    return names[type] || type.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
  };

  // Filter operations
  const filteredOperations = operations.filter(operation => {
    if (filters.status !== 'all' && operation.status !== filters.status) return false;
    if (filters.operation_type !== 'all' && operation.operation_type !== filters.operation_type) return false;
    if (filters.user !== 'all' && operation.user !== filters.user) return false;
    
    // Date range filtering
    const operationDate = new Date(operation.timestamp);
    const now = new Date();
    const diffInDays = (now.getTime() - operationDate.getTime()) / (1000 * 60 * 60 * 24);
    
    switch (filters.date_range) {
      case '1d': if (diffInDays > 1) return false; break;
      case '7d': if (diffInDays > 7) return false; break;
      case '30d': if (diffInDays > 30) return false; break;
      case '90d': if (diffInDays > 90) return false; break;
    }
    
    return true;
  });

  // Export history
  const exportHistory = () => {
    const csvContent = [
      ['Operation Type', 'Status', 'User', 'Timestamp', 'Documents Processed', 'Processing Time', 'Memory Usage'],
      ...filteredOperations.map(op => [
        getOperationDisplayName(op.operation_type),
        op.status,
        op.user,
        new Date(op.timestamp).toLocaleString(),
        op.details.documents_processed || 0,
        op.details.processing_time || 0,
        op.details.memory_usage || 0
      ])
    ].map(row => row.join(',')).join('\n');
    
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `normalization_history_${new Date().toISOString().split('T')[0]}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  useEffect(() => {
    fetchHistory();
  }, [fetchHistory]);

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-medium text-gray-900">Normalization History</h3>
          <p className="text-sm text-gray-600">
            Track all normalization operations, audit trail, and rollback capabilities
          </p>
        </div>
        <div className="flex space-x-2">
          <button
            onClick={exportHistory}
            className="inline-flex items-center px-3 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
          >
            <Download className="h-4 w-4 mr-2" />
            Export CSV
          </button>
          <button
            onClick={fetchHistory}
            disabled={loading}
            className="inline-flex items-center px-3 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50"
          >
            <RefreshCw className={`h-4 w-4 mr-2 ${loading ? 'animate-spin' : ''}`} />
            Refresh
          </button>
        </div>
      </div>

      {/* Filters */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-6 py-4 border-b border-gray-200">
          <h4 className="text-lg font-medium text-gray-900">Filters</h4>
          <p className="text-sm text-gray-600">
            Filter operations by status, type, date range, and user
          </p>
        </div>
        
        <div className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Status</label>
              <select
                value={filters.status}
                onChange={(e) => setFilters(prev => ({ ...prev, status: e.target.value }))}
                className="block w-full border border-gray-300 rounded-md px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
              >
                <option value="all">All Statuses</option>
                <option value="success">Success</option>
                <option value="failed">Failed</option>
                <option value="in_progress">In Progress</option>
                <option value="cancelled">Cancelled</option>
              </select>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Operation Type</label>
              <select
                value={filters.operation_type}
                onChange={(e) => setFilters(prev => ({ ...prev, operation_type: e.target.value }))}
                className="block w-full border border-gray-300 rounded-md px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
              >
                <option value="all">All Types</option>
                <option value="statute_name_normalization">Statute Name Normalization</option>
                <option value="structure_cleaning">Structure Cleaning</option>
                <option value="alphabetical_sorting">Alphabetical Sorting</option>
                <option value="full_normalization">Full Normalization</option>
              </select>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Date Range</label>
              <select
                value={filters.date_range}
                onChange={(e) => setFilters(prev => ({ ...prev, date_range: e.target.value }))}
                className="block w-full border border-gray-300 rounded-md px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
              >
                <option value="1d">Last 24 Hours</option>
                <option value="7d">Last 7 Days</option>
                <option value="30d">Last 30 Days</option>
                <option value="90d">Last 90 Days</option>
                <option value="all">All Time</option>
              </select>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">User</label>
              <select
                value={filters.user}
                onChange={(e) => setFilters(prev => ({ ...prev, user: e.target.value }))}
                className="block w-full border border-gray-300 rounded-md px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
              >
                <option value="all">All Users</option>
                <option value="admin">Admin</option>
                <option value="system">System</option>
                <option value="user">User</option>
              </select>
            </div>
          </div>
        </div>
      </div>

      {/* Operations List */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-6 py-4 border-b border-gray-200">
          <div className="flex items-center justify-between">
            <div>
              <h4 className="text-lg font-medium text-gray-900">Operations History</h4>
              <p className="text-sm text-gray-600">
                {filteredOperations.length} operations found
              </p>
            </div>
          </div>
        </div>
        
        {loading ? (
          <div className="p-8 text-center">
            <RefreshCw className="h-8 w-8 text-gray-400 mx-auto mb-4 animate-spin" />
            <p className="text-gray-600">Loading operations history...</p>
          </div>
        ) : filteredOperations.length === 0 ? (
          <div className="p-8 text-center">
            <History className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <p className="text-gray-600">No operations found matching the current filters</p>
            <p className="text-sm text-gray-500">Try adjusting your filter criteria</p>
          </div>
        ) : (
          <div className="overflow-hidden">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Operation
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Status
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    User
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Timestamp
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Details
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {filteredOperations.map((operation) => (
                  <tr key={operation._id} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <div className="flex-shrink-0 h-8 w-8 bg-gray-100 rounded-lg flex items-center justify-center">
                          {getOperationIcon(operation.operation_type)}
                        </div>
                        <div className="ml-3">
                          <div className="text-sm font-medium text-gray-900">
                            {getOperationDisplayName(operation.operation_type)}
                          </div>
                          <div className="text-sm text-gray-500">
                            {operation.details.documents_processed || 0} documents
                          </div>
                        </div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor(operation.status)}`}>
                        {operation.status.replace('_', ' ')}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <User className="h-4 w-4 text-gray-400 mr-2" />
                        <span className="text-sm text-gray-900">{operation.user}</span>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <Calendar className="h-4 w-4 text-gray-400 mr-2" />
                        <span className="text-sm text-gray-900">
                          {new Date(operation.timestamp).toLocaleString()}
                        </span>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-900">
                        <div>Time: {operation.details.processing_time || 0}s</div>
                        <div>Memory: {operation.details.memory_usage || 0}MB</div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                      <div className="flex space-x-2">
                        <button
                          onClick={() => {
                            setSelectedOperation(operation);
                            setShowDetails(true);
                          }}
                          className="text-primary-600 hover:text-primary-900"
                        >
                          <Eye className="h-4 w-4" />
                        </button>
                        
                        {operation.rollback_available && operation.rollback_status === 'available' && (
                          <button
                            onClick={() => rollbackOperation(operation._id)}
                            disabled={rollbackLoading === operation._id}
                            className="text-red-600 hover:text-red-900 disabled:opacity-50"
                          >
                            <RotateCcw className={`h-4 w-4 ${rollbackLoading === operation._id ? 'animate-spin' : ''}`} />
                          </button>
                        )}
                        
                        {operation.rollback_status === 'completed' && (
                          <span className="text-green-600">
                            <CheckCircle className="h-4 w-4" />
                          </span>
                        )}
                        
                        {operation.rollback_status === 'failed' && (
                          <span className="text-red-600">
                            <AlertTriangle className="h-4 w-4" />
                          </span>
                        )}
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Operation Details Modal */}
      {showDetails && selectedOperation && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-11/12 md:w-3/4 lg:w-1/2 shadow-lg rounded-md bg-white">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-medium text-gray-900">
                Operation Details: {getOperationDisplayName(selectedOperation.operation_type)}
              </h3>
              <button
                onClick={() => setShowDetails(false)}
                className="text-gray-400 hover:text-gray-600"
              >
                <X className="h-6 w-6" />
              </button>
            </div>
            
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700">Status</label>
                  <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor(selectedOperation.status)}`}>
                    {selectedOperation.status.replace('_', ' ')}
                  </span>
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700">User</label>
                  <span className="text-sm text-gray-900">{selectedOperation.user}</span>
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700">Timestamp</label>
                  <span className="text-sm text-gray-900">
                    {new Date(selectedOperation.timestamp).toLocaleString()}
                  </span>
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700">Documents Processed</label>
                  <span className="text-sm text-gray-900">
                    {selectedOperation.details.documents_processed || 0}
                  </span>
                </div>
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Details</label>
                <div className="bg-gray-50 rounded-lg p-3 text-sm">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <span className="text-gray-600">Source Collection:</span>
                      <span className="ml-2 text-gray-900">{selectedOperation.details.source_collection || 'N/A'}</span>
                    </div>
                    <div>
                      <span className="text-gray-600">Target Collection:</span>
                      <span className="ml-2 text-gray-900">{selectedOperation.details.target_collection || 'N/A'}</span>
                    </div>
                    <div>
                      <span className="text-gray-600">Processing Time:</span>
                      <span className="ml-2 text-gray-900">{selectedOperation.details.processing_time || 0}s</span>
                    </div>
                    <div>
                      <span className="text-gray-600">Memory Usage:</span>
                      <span className="ml-2 text-gray-900">{selectedOperation.details.memory_usage || 0}MB</span>
                    </div>
                  </div>
                  
                  {selectedOperation.details.errors && selectedOperation.details.errors.length > 0 && (
                    <div className="mt-3">
                      <span className="text-red-600 font-medium">Errors:</span>
                      <ul className="mt-1 text-red-600">
                        {selectedOperation.details.errors.map((error, index) => (
                          <li key={index} className="text-sm">â€¢ {error}</li>
                        ))}
                      </ul>
                    </div>
                  )}
                  
                  {selectedOperation.details.warnings && selectedOperation.details.warnings.length > 0 && (
                    <div className="mt-3">
                      <span className="text-yellow-600 font-medium">Warnings:</span>
                      <ul className="mt-1 text-yellow-600">
                        {selectedOperation.details.warnings.map((warning, index) => (
                          <li key={index} className="text-sm">â€¢ {warning}</li>
                        ))}
                      </ul>
                    </div>
                  )}
                </div>
              </div>
              
              <div className="flex justify-end space-x-3 pt-4 border-t">
                <button
                  onClick={() => setShowDetails(false)}
                  className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
                >
                  Close
                </button>
                
                {selectedOperation.rollback_available && selectedOperation.rollback_status === 'available' && (
                  <button
                    onClick={() => {
                      rollbackOperation(selectedOperation._id);
                      setShowDetails(false);
                    }}
                    disabled={rollbackLoading === selectedOperation._id}
                    className="px-4 py-2 border border-transparent rounded-md text-sm font-medium text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500 disabled:opacity-50"
                  >
                    <RotateCcw className={`h-4 w-4 mr-2 inline ${rollbackLoading === selectedOperation._id ? 'animate-spin' : ''}`} />
                    Rollback
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Summary */}
      <div className="bg-gray-50 rounded-lg p-4">
        <h4 className="text-sm font-medium text-gray-900 mb-2">History Summary</h4>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 text-sm">
          <div>
            <span className="text-gray-600">Total Operations:</span>
            <span className="ml-2 font-medium text-gray-900">{operations.length}</span>
          </div>
          <div>
            <span className="text-gray-600">Successful:</span>
            <span className="ml-2 font-medium text-green-600">
              {operations.filter(op => op.status === 'success').length}
            </span>
          </div>
          <div>
            <span className="text-gray-600">Failed:</span>
            <span className="ml-2 font-medium text-red-600">
              {operations.filter(op => op.status === 'failed').length}
            </span>
          </div>
          <div>
            <span className="text-gray-600">Rollback Available:</span>
            <span className="ml-2 font-medium text-blue-600">
              {operations.filter(op => op.rollback_available).length}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NormalizationHistory;
