import React, { useState, useEffect, useCallback } from 'react';
import { FileText, Edit3, Save, X, Search, RefreshCw, AlertTriangle, CheckCircle } from 'lucide-react';

interface StatuteName {
  _id: string;
  count: number;
}

interface StatuteNameNormalizerProps {
  config: any;
}

const StatuteNameNormalizer: React.FC<StatuteNameNormalizerProps> = ({ config }) => {
  const [statuteNames, setStatuteNames] = useState<StatuteName[]>([]);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editingName, setEditingName] = useState('');
  const [message, setMessage] = useState<{ type: 'success' | 'error'; text: string } | null>(null);
  const [selectedNames, setSelectedNames] = useState<Set<string>>(new Set());
  const [batchEditMode, setBatchEditMode] = useState(false);
  const [batchNewName, setBatchNewName] = useState('');

  // Fetch statute names
  const fetchStatuteNames = useCallback(async () => {
    setLoading(true);
    try {
      const response = await fetch('/api/v1/phase2/statute-names?limit=1000');
      const data = await response.json();
      
      if (data.success) {
        setStatuteNames(data.statute_names);
      } else {
        setMessage({ type: 'error', text: 'Failed to fetch statute names' });
      }
    } catch (error) {
      setMessage({ type: 'error', text: 'Error fetching statute names' });
    } finally {
      setLoading(false);
    }
  }, []);

  // Update a single statute name
  const updateStatuteName = async (oldName: string, newName: string) => {
    try {
      const response = await fetch('/api/v1/phase2/update-statute-name', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ old_name: oldName, new_name: newName })
      });
      
      const data = await response.json();
      
      if (data.success) {
        setMessage({ type: 'success', text: `Updated ${data.modified_count} documents` });
        fetchStatuteNames(); // Refresh the list
      } else {
        setMessage({ type: 'error', text: 'Failed to update statute name' });
      }
    } catch (error) {
      setMessage({ type: 'error', text: 'Error updating statute name' });
    }
  };

  // Handle edit start
  const startEdit = (statuteName: StatuteName) => {
    setEditingId(statuteName._id);
    setEditingName(statuteName._id);
  };

  // Handle edit save
  const saveEdit = async () => {
    if (editingId && editingName.trim()) {
      await updateStatuteName(editingId, editingName.trim());
      setEditingId(null);
      setEditingName('');
    }
  };

  // Handle edit cancel
  const cancelEdit = () => {
    setEditingId(null);
    setEditingName('');
  };

  // Handle batch selection
  const toggleSelection = (name: string) => {
    const newSelection = new Set(selectedNames);
    if (newSelection.has(name)) {
      newSelection.delete(name);
    } else {
      newSelection.add(name);
    }
    setSelectedNames(newSelection);
  };

  // Handle batch edit
  const handleBatchEdit = async () => {
    if (selectedNames.size === 0 || !batchNewName.trim()) {
      setMessage({ type: 'error', text: 'Please select names and enter a new name' });
      return;
    }

    setLoading(true);
    let successCount = 0;
    let errorCount = 0;

    for (const oldName of selectedNames) {
      try {
        const response = await fetch('/api/v1/phase2/update-statute-name', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ old_name: oldName, new_name: batchNewName.trim() })
        });
        
        const data = await response.json();
        if (data.success) {
          successCount += data.modified_count;
        } else {
          errorCount++;
        }
      } catch (error) {
        errorCount++;
      }
    }

    setLoading(false);
    setMessage({ 
      type: successCount > 0 ? 'success' : 'error', 
      text: `Batch update completed: ${successCount} successful, ${errorCount} failed` 
    });
    
    setSelectedNames(new Set());
    setBatchNewName('');
    setBatchEditMode(false);
    fetchStatuteNames();
  };

  // Filter statute names based on search term
  const filteredNames = statuteNames.filter(name => 
    name._id.toLowerCase().includes(searchTerm.toLowerCase())
  );

  useEffect(() => {
    fetchStatuteNames();
  }, [fetchStatuteNames]);

  // Clear message after 5 seconds
  useEffect(() => {
    if (message) {
      const timer = setTimeout(() => setMessage(null), 5000);
      return () => clearTimeout(timer);
    }
  }, [message]);

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-medium text-gray-900">Statute Name Normalization</h3>
          <p className="text-sm text-gray-600">
            Analyze, edit, and standardize statute names across all documents
          </p>
        </div>
        <div className="flex space-x-2">
          <button
            onClick={fetchStatuteNames}
            disabled={loading}
            className="inline-flex items-center px-3 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50"
          >
            <RefreshCw className={`h-4 w-4 mr-2 ${loading ? 'animate-spin' : ''}`} />
            Refresh
          </button>
          <button
            onClick={() => setBatchEditMode(!batchEditMode)}
            className={`inline-flex items-center px-3 py-2 border text-sm font-medium rounded-md ${
              batchEditMode 
                ? 'border-red-300 text-red-700 bg-red-50 hover:bg-red-100' 
                : 'border-gray-300 text-gray-700 bg-white hover:bg-gray-50'
            } focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500`}
          >
            {batchEditMode ? 'Cancel Batch' : 'Batch Edit'}
          </button>
        </div>
      </div>

      {/* Message Display */}
      {message && (
        <div className={`rounded-md p-4 ${
          message.type === 'success' ? 'bg-green-50 border border-green-200' : 'bg-red-50 border border-red-200'
        }`}>
          <div className="flex">
            <div className="flex-shrink-0">
              {message.type === 'success' ? (
                <CheckCircle className="h-5 w-5 text-green-400" />
              ) : (
                <AlertTriangle className="h-5 w-5 text-red-400" />
              )}
            </div>
            <div className="ml-3">
              <p className={`text-sm ${
                message.type === 'success' ? 'text-green-800' : 'text-red-800'
              }`}>
                {message.text}
              </p>
            </div>
          </div>
        </div>
      )}

      {/* Batch Edit Mode */}
      {batchEditMode && (
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
          <h4 className="text-sm font-medium text-blue-800 mb-3">Batch Edit Mode</h4>
          <div className="flex items-center space-x-4">
            <div className="flex-1">
              <label htmlFor="batch-new-name" className="block text-sm font-medium text-gray-700 mb-1">
                New Name for Selected ({selectedNames.size} selected)
              </label>
              <input
                type="text"
                id="batch-new-name"
                value={batchNewName}
                onChange={(e) => setBatchNewName(e.target.value)}
                placeholder="Enter new statute name"
                className="block w-full border border-gray-300 rounded-md px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
              />
            </div>
            <button
              onClick={handleBatchEdit}
              disabled={selectedNames.size === 0 || !batchNewName.trim() || loading}
              className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <Save className="h-4 w-4 mr-2" />
              Update Selected
            </button>
          </div>
        </div>
      )}

      {/* Search and Stats */}
      <div className="flex items-center justify-between">
        <div className="flex-1 max-w-md">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            <input
              type="text"
              placeholder="Search statute names..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
            />
          </div>
        </div>
        <div className="text-sm text-gray-600">
          {filteredNames.length} of {statuteNames.length} names
        </div>
      </div>

      {/* Statute Names List */}
      <div className="bg-white shadow rounded-lg">
        {loading ? (
          <div className="p-8 text-center">
            <RefreshCw className="h-8 w-8 text-gray-400 mx-auto mb-4 animate-spin" />
            <p className="text-gray-600">Loading statute names...</p>
          </div>
        ) : filteredNames.length === 0 ? (
          <div className="p-8 text-center">
            <FileText className="h-8 w-8 text-gray-400 mx-auto mb-4" />
            <p className="text-gray-600">
              {searchTerm ? 'No statute names match your search' : 'No statute names found'}
            </p>
          </div>
        ) : (
          <div className="overflow-hidden">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  {batchEditMode && (
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      <input
                        type="checkbox"
                        checked={selectedNames.size === filteredNames.length}
                        onChange={(e) => {
                          if (e.target.checked) {
                            setSelectedNames(new Set(filteredNames.map(n => n._id)));
                          } else {
                            setSelectedNames(new Set());
                          }
                        }}
                        className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                      />
                    </th>
                  )}
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Statute Name
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Document Count
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {filteredNames.map((statuteName) => (
                  <tr key={statuteName._id} className="hover:bg-gray-50">
                    {batchEditMode && (
                      <td className="px-6 py-4 whitespace-nowrap">
                        <input
                          type="checkbox"
                          checked={selectedNames.has(statuteName._id)}
                          onChange={() => toggleSelection(statuteName._id)}
                          className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                        />
                      </td>
                    )}
                    <td className="px-6 py-4 whitespace-nowrap">
                      {editingId === statuteName._id ? (
                        <input
                          type="text"
                          value={editingName}
                          onChange={(e) => setEditingName(e.target.value)}
                          className="block w-full border border-gray-300 rounded-md px-2 py-1 text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
                        />
                      ) : (
                        <span className="text-sm font-medium text-gray-900">
                          {statuteName._id}
                        </span>
                      )}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                        {statuteName.count}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                      {editingId === statuteName._id ? (
                        <div className="flex space-x-2">
                          <button
                            onClick={saveEdit}
                            className="text-green-600 hover:text-green-900"
                          >
                            <Save className="h-4 w-4" />
                          </button>
                          <button
                            onClick={cancelEdit}
                            className="text-red-600 hover:text-red-900"
                          >
                            <X className="h-4 w-4" />
                          </button>
                        </div>
                      ) : (
                        <button
                          onClick={() => startEdit(statuteName)}
                          className="text-primary-600 hover:text-primary-900"
                        >
                          <Edit3 className="h-4 w-4" />
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Summary */}
      <div className="bg-gray-50 rounded-lg p-4">
        <h4 className="text-sm font-medium text-gray-900 mb-2">Summary</h4>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
          <div>
            <span className="text-gray-600">Total Unique Names:</span>
            <span className="ml-2 font-medium text-gray-900">{statuteNames.length}</span>
          </div>
          <div>
            <span className="text-gray-600">Total Documents:</span>
            <span className="ml-2 font-medium text-gray-900">
              {statuteNames.reduce((sum, name) => sum + name.count, 0)}
            </span>
          </div>
          <div>
            <span className="text-gray-600">Average per Name:</span>
            <span className="ml-2 font-medium text-gray-900">
              {statuteNames.length > 0 
                ? Math.round(statuteNames.reduce((sum, name) => sum + name.count, 0) / statuteNames.length)
                : 0
              }
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StatuteNameNormalizer;
