import React, { useState, useEffect, useCallback } from 'react';
import { Activity, BarChart3, Clock, CheckCircle, AlertTriangle, RefreshCw, Play, Pause, Square } from 'lucide-react';

interface ProgressStatus {
  raw_statutes: {
    exists: boolean;
    count: number;
    status: string;
  };
  normalized_statutes: {
    exists: boolean;
    count: number;
    status: string;
  };
  cleaned_statutes: {
    exists: boolean;
    count: number;
    status: string;
  };
  sorted_statutes: {
    exists: boolean;
    count: number;
    status: string;
  };
}

interface OverallProgress {
  completed_steps: number;
  total_steps: number;
  percentage: number;
}

interface ProgressTrackerProps {
  config: any;
}

const ProgressTracker: React.FC<ProgressTrackerProps> = ({ config }) => {
  const [progressStatus, setProgressStatus] = useState<ProgressStatus | null>(null);
  const [overallProgress, setOverallProgress] = useState<OverallProgress | null>(null);
  const [loading, setLoading] = useState(false);
  const [autoRefresh, setAutoRefresh] = useState(true);
  const [refreshInterval, setRefreshInterval] = useState(5000); // 5 seconds
  const [processingStats, setProcessingStats] = useState<any>(null);
  const [performanceMetrics, setPerformanceMetrics] = useState<any>(null);
  const [errorLogs, setErrorLogs] = useState<any[]>([]);

  // Fetch progress status
  const fetchProgressStatus = useCallback(async () => {
    setLoading(true);
    try {
      const response = await fetch('/api/v1/phase2/progress-status');
      const data = await response.json();
      
      if (data.success) {
        setProgressStatus(data.progress_status);
        setOverallProgress(data.overall_progress);
      } else {
        console.error('Failed to fetch progress status');
      }
    } catch (error) {
      console.error('Error fetching progress status:', error);
    } finally {
      setLoading(false);
    }
  }, []);

  // Simulate processing statistics
  const generateProcessingStats = () => {
    const stats = {
      documents_processed: Math.floor(Math.random() * 1000) + 100,
      processing_rate: Math.floor(Math.random() * 100) + 10,
      estimated_completion: new Date(Date.now() + Math.random() * 60000).toLocaleTimeString(),
      errors_encountered: Math.floor(Math.random() * 10),
      warnings: Math.floor(Math.random() * 20),
      memory_usage: Math.floor(Math.random() * 80) + 20,
      cpu_usage: Math.floor(Math.random() * 60) + 20
    };
    setProcessingStats(stats);
  };

  // Simulate performance metrics
  const generatePerformanceMetrics = () => {
    const metrics = {
      total_processing_time: Math.floor(Math.random() * 300) + 60,
      average_document_time: (Math.random() * 0.5 + 0.1).toFixed(3),
      throughput: Math.floor(Math.random() * 500) + 100,
      peak_memory: Math.floor(Math.random() * 100) + 50,
      database_operations: Math.floor(Math.random() * 1000) + 500,
      cache_hit_rate: Math.floor(Math.random() * 30) + 70
    };
    setPerformanceMetrics(metrics);
  };

  // Simulate error logs
  const generateErrorLogs = () => {
    const logs = [
      {
        timestamp: new Date(Date.now() - Math.random() * 3600000).toISOString(),
        level: 'ERROR',
        message: 'Failed to process document ID: 12345',
        details: 'MongoDB connection timeout'
      },
      {
        timestamp: new Date(Date.now() - Math.random() * 1800000).toISOString(),
        level: 'WARNING',
        message: 'Field validation failed for document ID: 67890',
        details: 'Required field missing: statute_name'
      },
      {
        timestamp: new Date(Date.now() - Math.random() * 900000).toISOString(),
        level: 'INFO',
        message: 'Batch processing completed',
        details: 'Processed 100 documents successfully'
      }
    ];
    setErrorLogs(logs);
  };

  // Auto-refresh effect
  useEffect(() => {
    if (autoRefresh) {
      const interval = setInterval(() => {
        fetchProgressStatus();
        generateProcessingStats();
        generatePerformanceMetrics();
        generateErrorLogs();
      }, refreshInterval);
      
      return () => clearInterval(interval);
    }
  }, [autoRefresh, refreshInterval, fetchProgressStatus]);

  // Initial fetch
  useEffect(() => {
    fetchProgressStatus();
    generateProcessingStats();
    generatePerformanceMetrics();
    generateErrorLogs();
  }, [fetchProgressStatus]);

  // Get status color
  const getStatusColor = (status: string) => {
    const colors: { [key: string]: string } = {
      'source': 'bg-blue-100 text-blue-800',
      'normalized': 'bg-green-100 text-green-800',
      'cleaned': 'bg-yellow-100 text-yellow-800',
      'sorted': 'bg-purple-100 text-purple-800',
      'pending': 'bg-gray-100 text-gray-800'
    };
    return colors[status] || 'bg-gray-100 text-gray-800';
  };

  // Get step icon
  const getStepIcon = (status: string) => {
    if (status === 'pending') return <Clock className="h-5 w-5 text-gray-400" />;
    if (status === 'source') return <Activity className="h-5 w-5 text-blue-500" />;
    return <CheckCircle className="h-5 w-5 text-green-500" />;
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-medium text-gray-900">Progress Tracker</h3>
          <p className="text-sm text-gray-600">
            Real-time monitoring of normalization progress and performance
          </p>
        </div>
        <div className="flex items-center space-x-4">
          <div className="flex items-center space-x-2">
            <label className="text-sm text-gray-700">Auto-refresh:</label>
            <input
              type="checkbox"
              checked={autoRefresh}
              onChange={(e) => setAutoRefresh(e.target.checked)}
              className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
            />
          </div>
          <select
            value={refreshInterval}
            onChange={(e) => setRefreshInterval(parseInt(e.target.value))}
            className="border border-gray-300 rounded-md px-3 py-1 text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
          >
            <option value={2000}>2s</option>
            <option value={5000}>5s</option>
            <option value={10000}>10s</option>
            <option value={30000}>30s</option>
          </select>
          <button
            onClick={fetchProgressStatus}
            disabled={loading}
            className="inline-flex items-center px-3 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50"
          >
            <RefreshCw className={`h-4 w-4 mr-2 ${loading ? 'animate-spin' : ''}`} />
            Refresh
          </button>
        </div>
      </div>

      {/* Overall Progress */}
      {overallProgress && (
        <div className="bg-white shadow rounded-lg">
          <div className="px-6 py-4 border-b border-gray-200">
            <h4 className="text-lg font-medium text-gray-900">Overall Progress</h4>
            <p className="text-sm text-gray-600">
              {overallProgress.completed_steps} of {overallProgress.total_steps} steps completed
            </p>
          </div>
          
          <div className="p-6">
            <div className="mb-4">
              <div className="flex items-center justify-between mb-2">
                <span className="text-sm font-medium text-gray-700">Progress</span>
                <span className="text-sm font-medium text-gray-900">{overallProgress.percentage}%</span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-4">
                <div 
                  className="bg-gradient-to-r from-blue-500 to-green-500 h-4 rounded-full transition-all duration-500 ease-out"
                  style={{ width: `${overallProgress.percentage}%` }}
                />
              </div>
            </div>
            
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-center">
              <div className="bg-blue-50 rounded-lg p-4">
                <div className="text-2xl font-bold text-blue-600">{overallProgress.completed_steps}</div>
                <div className="text-sm text-blue-800">Steps Completed</div>
              </div>
              <div className="bg-yellow-50 rounded-lg p-4">
                <div className="text-2xl font-bold text-yellow-600">{overallProgress.total_steps - overallProgress.completed_steps}</div>
                <div className="text-sm text-yellow-800">Steps Remaining</div>
              </div>
              <div className="bg-green-50 rounded-lg p-4">
                <div className="text-2xl font-bold text-green-600">{overallProgress.percentage}%</div>
                <div className="text-sm text-green-800">Completion Rate</div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Step-by-Step Progress */}
      {progressStatus && (
        <div className="bg-white shadow rounded-lg">
          <div className="px-6 py-4 border-b border-gray-200">
            <h4 className="text-lg font-medium text-gray-900">Step-by-Step Progress</h4>
            <p className="text-sm text-gray-600">
              Detailed status of each normalization step
            </p>
          </div>
          
          <div className="p-6">
            <div className="space-y-6">
              {/* Raw Statutes */}
              <div className="flex items-center space-x-4">
                <div className="flex-shrink-0">
                  {getStepIcon(progressStatus.raw_statutes.status)}
                </div>
                <div className="flex-1">
                  <h5 className="text-sm font-medium text-gray-900">Raw Statutes</h5>
                  <p className="text-sm text-gray-600">
                    Source data collection with {progressStatus.raw_statutes.count} documents
                  </p>
                </div>
                <div className="flex-shrink-0">
                  <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor(progressStatus.raw_statutes.status)}`}>
                    {progressStatus.raw_statutes.status}
                  </span>
                </div>
              </div>

              {/* Normalized Statutes */}
              <div className="flex items-center space-x-4">
                <div className="flex-shrink-0">
                  {getStepIcon(progressStatus.normalized_statutes.status)}
                </div>
                <div className="flex-1">
                  <h5 className="text-sm font-medium text-gray-900">Statute Name Normalization</h5>
                  <p className="text-sm text-gray-600">
                    {progressStatus.normalized_statutes.exists 
                      ? `${progressStatus.normalized_statutes.count} documents normalized`
                      : 'Pending - will process raw statutes'
                    }
                  </p>
                </div>
                <div className="flex-shrink-0">
                  <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor(progressStatus.normalized_statutes.status)}`}>
                    {progressStatus.normalized_statutes.status}
                  </span>
                </div>
              </div>

              {/* Cleaned Statutes */}
              <div className="flex items-center space-x-4">
                <div className="flex-shrink-0">
                  {getStepIcon(progressStatus.cleaned_statutes.status)}
                </div>
                <div className="flex-1">
                  <h5 className="text-sm font-medium text-gray-900">Structure Cleaning</h5>
                  <p className="text-sm text-gray-600">
                    {progressStatus.cleaned_statutes.exists 
                      ? `${progressStatus.cleaned_statutes.count} documents cleaned`
                      : 'Pending - will process normalized statutes'
                    }
                  </p>
                </div>
                <div className="flex-shrink-0">
                  <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor(progressStatus.cleaned_statutes.status)}`}>
                    {progressStatus.cleaned_statutes.status}
                  </span>
                </div>
              </div>

              {/* Sorted Statutes */}
              <div className="flex items-center space-x-4">
                <div className="flex-shrink-0">
                  {getStepIcon(progressStatus.sorted_statutes.status)}
                </div>
                <div className="flex-1">
                  <h5 className="text-sm font-medium text-gray-900">Alphabetical Sorting</h5>
                  <p className="text-sm text-gray-600">
                    {progressStatus.sorted_statutes.exists 
                      ? `${progressStatus.sorted_statutes.count} documents sorted`
                      : 'Pending - will process cleaned statutes'
                    }
                  </p>
                </div>
                <div className="flex-shrink-0">
                  <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor(progressStatus.sorted_statutes.status)}`}>
                    {progressStatus.sorted_statutes.status}
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Processing Statistics */}
      {processingStats && (
        <div className="bg-white shadow rounded-lg">
          <div className="px-6 py-4 border-b border-gray-200">
            <h4 className="text-lg font-medium text-gray-900">Processing Statistics</h4>
            <p className="text-sm text-gray-600">
              Real-time processing metrics and performance indicators
            </p>
          </div>
          
          <div className="p-6">
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              <div className="bg-blue-50 rounded-lg p-4">
                <div className="flex items-center">
                  <BarChart3 className="h-8 w-8 text-blue-500" />
                  <div className="ml-3">
                    <div className="text-2xl font-bold text-blue-600">{processingStats.documents_processed}</div>
                    <div className="text-sm text-blue-800">Documents Processed</div>
                  </div>
                </div>
              </div>
              
              <div className="bg-green-50 rounded-lg p-4">
                <div className="flex items-center">
                  <Activity className="h-8 w-8 text-green-500" />
                  <div className="ml-3">
                    <div className="text-2xl font-bold text-green-600">{processingStats.processing_rate}</div>
                    <div className="text-sm text-green-800">Docs/Second</div>
                  </div>
                </div>
              </div>
              
              <div className="bg-yellow-50 rounded-lg p-4">
                <div className="flex items-center">
                  <Clock className="h-8 w-8 text-yellow-500" />
                  <div className="ml-3">
                    <div className="text-lg font-bold text-yellow-600">{processingStats.estimated_completion}</div>
                    <div className="text-sm text-yellow-800">Est. Completion</div>
                  </div>
                </div>
              </div>
              
              <div className="bg-red-50 rounded-lg p-4">
                <div className="flex items-center">
                  <AlertTriangle className="h-8 w-8 text-red-500" />
                  <div className="ml-3">
                    <div className="text-2xl font-bold text-red-600">{processingStats.errors_encountered}</div>
                    <div className="text-sm text-red-800">Errors</div>
                  </div>
                </div>
              </div>
            </div>
            
            <div className="mt-6 grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <h5 className="text-sm font-medium text-gray-900 mb-3">System Resources</h5>
                <div className="space-y-3">
                  <div>
                    <div className="flex items-center justify-between mb-1">
                      <span className="text-sm text-gray-600">Memory Usage</span>
                      <span className="text-sm font-medium text-gray-900">{processingStats.memory_usage}%</span>
                    </div>
                    <div className="w-full bg-gray-200 rounded-full h-2">
                      <div 
                        className={`h-2 rounded-full ${processingStats.memory_usage > 80 ? 'bg-red-500' : processingStats.memory_usage > 60 ? 'bg-yellow-500' : 'bg-green-500'}`}
                        style={{ width: `${processingStats.memory_usage}%` }}
                      />
                    </div>
                  </div>
                  
                  <div>
                    <div className="flex items-center justify-between mb-1">
                      <span className="text-sm text-gray-600">CPU Usage</span>
                      <span className="text-sm font-medium text-gray-900">{processingStats.cpu_usage}%</span>
                    </div>
                    <div className="w-full bg-gray-200 rounded-full h-2">
                      <div 
                        className={`h-2 rounded-full ${processingStats.cpu_usage > 80 ? 'bg-red-500' : processingStats.cpu_usage > 60 ? 'bg-yellow-500' : 'bg-green-500'}`}
                        style={{ width: `${processingStats.cpu_usage}%` }}
                      />
                    </div>
                  </div>
                </div>
              </div>
              
              <div>
                <h5 className="text-sm font-medium text-gray-900 mb-3">Processing Details</h5>
                <div className="space-y-2 text-sm">
                  <div className="flex justify-between">
                    <span className="text-gray-600">Warnings:</span>
                    <span className="font-medium text-gray-900">{processingStats.warnings}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Status:</span>
                    <span className="font-medium text-green-600">Active</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Last Update:</span>
                    <span className="font-medium text-gray-900">{new Date().toLocaleTimeString()}</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Performance Metrics */}
      {performanceMetrics && (
        <div className="bg-white shadow rounded-lg">
          <div className="px-6 py-4 border-b border-gray-200">
            <h4 className="text-lg font-medium text-gray-900">Performance Metrics</h4>
            <p className="text-sm text-gray-600">
              Historical performance data and optimization insights
            </p>
          </div>
          
          <div className="p-6">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="text-center">
                <div className="text-3xl font-bold text-blue-600">{performanceMetrics.total_processing_time}s</div>
                <div className="text-sm text-gray-600">Total Processing Time</div>
              </div>
              
              <div className="text-center">
                <div className="text-3xl font-bold text-green-600">{performanceMetrics.throughput}</div>
                <div className="text-sm text-gray-600">Documents/Second</div>
              </div>
              
              <div className="text-center">
                <div className="text-3xl font-bold text-purple-600">{performanceMetrics.cache_hit_rate}%</div>
                <div className="text-sm text-gray-600">Cache Hit Rate</div>
              </div>
            </div>
            
            <div className="mt-6 grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <h5 className="text-sm font-medium text-gray-900 mb-3">Efficiency Metrics</h5>
                <div className="space-y-3 text-sm">
                  <div className="flex justify-between">
                    <span className="text-gray-600">Avg Document Time:</span>
                    <span className="font-medium text-gray-900">{performanceMetrics.average_document_time}s</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Peak Memory:</span>
                    <span className="font-medium text-gray-900">{performanceMetrics.peak_memory}MB</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">DB Operations:</span>
                    <span className="font-medium text-gray-900">{performanceMetrics.database_operations}</span>
                  </div>
                </div>
              </div>
              
              <div>
                <h5 className="text-sm font-medium text-gray-900 mb-3">Optimization Tips</h5>
                <div className="space-y-2 text-sm text-gray-600">
                  <div className="flex items-start space-x-2">
                    <CheckCircle className="h-4 w-4 text-green-500 mt-0.5 flex-shrink-0" />
                    <span>Database indexes are properly configured</span>
                  </div>
                  <div className="flex items-start space-x-2">
                    <CheckCircle className="h-4 w-4 text-green-500 mt-0.5 flex-shrink-0" />
                    <span>Batch processing is enabled</span>
                  </div>
                  <div className="flex items-start space-x-2">
                    <AlertTriangle className="h-4 w-4 text-yellow-500 mt-0.5 flex-shrink-0" />
                    <span>Consider increasing batch size for better throughput</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Error Logs */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-6 py-4 border-b border-gray-200">
          <h4 className="text-lg font-medium text-gray-900">Error Logs & Warnings</h4>
          <p className="text-sm text-gray-600">
            Recent processing errors, warnings, and informational messages
          </p>
        </div>
        
        <div className="p-6">
          {errorLogs.length === 0 ? (
            <div className="text-center py-8">
              <CheckCircle className="h-12 w-12 text-green-400 mx-auto mb-4" />
              <p className="text-gray-600">No errors or warnings to display</p>
              <p className="text-sm text-gray-500">Processing is running smoothly</p>
            </div>
          ) : (
            <div className="space-y-3">
              {errorLogs.map((log, index) => (
                <div key={index} className={`p-4 rounded-lg border ${
                  log.level === 'ERROR' ? 'bg-red-50 border-red-200' :
                  log.level === 'WARNING' ? 'bg-yellow-50 border-yellow-200' :
                  'bg-blue-50 border-blue-200'
                }`}>
                  <div className="flex items-start space-x-3">
                    <div className="flex-shrink-0">
                      {log.level === 'ERROR' ? (
                        <AlertTriangle className="h-5 w-5 text-red-400" />
                      ) : log.level === 'WARNING' ? (
                        <AlertTriangle className="h-5 w-5 text-yellow-400" />
                      ) : (
                        <CheckCircle className="h-5 w-5 text-blue-400" />
                      )}
                    </div>
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center justify-between">
                        <p className={`text-sm font-medium ${
                          log.level === 'ERROR' ? 'text-red-800' :
                          log.level === 'WARNING' ? 'text-yellow-800' :
                          'text-blue-800'
                        }`}>
                          {log.message}
                        </p>
                        <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                          log.level === 'ERROR' ? 'bg-red-100 text-red-800' :
                          log.level === 'WARNING' ? 'bg-yellow-100 text-yellow-800' :
                          'bg-blue-100 text-blue-800'
                        }`}>
                          {log.level}
                        </span>
                      </div>
                      <p className="text-sm text-gray-600 mt-1">{log.details}</p>
                      <p className="text-xs text-gray-500 mt-2">
                        {new Date(log.timestamp).toLocaleString()}
                      </p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProgressTracker;
